$(function(){$(".remove").click(function(){let t=$(this).attr("data-song-id");window.location.replace("?remove="+t)}),$("#order-by-select").change(function(){let t=$(this).val();window.location.replace("?orderby="+t)}),$("#select-artist").change(function(){$("#artist").val($(this).val())}),$("#select-album").change(function(){$("#album").val($(this).val())}),$("#select-song").change(function(){$("#song").val($(this).val())})});